		for (var i = 0 ; i < highestTimeoutId ; i++) {
			clearTimeout(i);
		}

		$(FrameTrail.getState('target')).find('.viewVideo').remove();
		FrameTrail.changeState('viewMode', 'video');

		FrameTrail.module('RouteNavigation').hypervideoID = newHypervideoID;
